def isValid(s):
    if len(s) <= 0:
        return 'NO'

    cnt = {}
    for c in s:
        if c in cnt.keys():
            cnt[c] += 1
        else:
            cnt[c] = 1

    print(cnt)
    oddcnt = 0
    values = list(cnt.values())
    prev = values[0]
    for i in range(1, len(values)):
        if prev == values[i]:
            continue
        elif values[i] ==1:
            continue
        elif abs(prev - values[i]) == 1:
            oddcnt += 1
            i += 1
        else:
            return "NO"

    print(oddcnt)
    if oddcnt > 1:
        return 'NO'
    else:
        return 'YES'


